import React, { useState } from "react";
import styled from "styled-components";
import { FaPlay, FaPlus, FaThumbsUp, FaThumbsDown } from "react-icons/fa";

const Row = styled.div`
  margin-left: 20px;
  color: white;
`;

const MoviePosters = styled.div`
  display: flex;
  overflow-y: hidden;
  overflow-x: scroll;
  padding: 20px;

  &::-webkit-scrollbar {
    display: none;
  }
`;

const PosterWrapper = styled.div`
  position: relative;
  margin-right: 10px;
  transition: transform 450ms;

  &:hover {
    transform: scale(1.08);
    z-index: 99;
  }
`;

const MoviePoster = styled.img`
  object-fit: contain;
  width: 100%;
  max-height: 250px;
`;

const MovieInfo = styled.div`
  display: none;
  position: absolute;
  bottom: 0;
  padding: 10px;
  background: rgba(0, 0, 0, 0.8);
  width: 100%;

  ${PosterWrapper}:hover & {
    display: block;
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 10px;
  margin-bottom: 10px;
`;

const IconButton = styled.button`
  background: rgba(255, 255, 255, 0.2);
  border: none;
  border-radius: 50%;
  padding: 8px;
  cursor: pointer;
  color: white;

  &:hover {
    background: rgba(255, 255, 255, 0.3);
  }
`;

const Title = styled.h2`
  margin-left: 20px;
`;

function MovieRow({ title, movies }) {
  return (
    <Row>
      <Title>{title}</Title>
      <MoviePosters>
        {movies.map((movie) => (
          <PosterWrapper key={movie.id}>
            <MoviePoster src={movie.poster} alt={movie.title} />
            <MovieInfo>
              <ButtonGroup>
                <IconButton>
                  <FaPlay />
                </IconButton>
                <IconButton>
                  <FaPlus />
                </IconButton>
                <IconButton>
                  <FaThumbsUp />
                </IconButton>
                <IconButton>
                  <FaThumbsDown />
                </IconButton>
              </ButtonGroup>
              <h4>{movie.title}</h4>
            </MovieInfo>
          </PosterWrapper>
        ))}
      </MoviePosters>
    </Row>
  );
}

export default MovieRow;
